function motionPointerInit(t){let e=document.createElement("div");e.classList.add("motionPointer"),document.querySelector("body").appendChild(e);const o=document.querySelector(".motionPointer");let n=o.offsetWidth,i=o.offsetHeight;if(o.style.left=-(n-6)/2+"px",o.style.top=-(i-6)/2+"px",t){let e=[];t.forEach(t=>{let o=document.querySelectorAll(t);return e.push(o),e}),e.forEach(t=>{t.forEach(t=>{t.addEventListener("mouseenter",(function(){o.classList.add("motionPointer--hover"),o.animate({width:[n+"px",2*n+"px"],height:[i+"px",2*i+"px"],left:[-(n-6)/2+"px",-(2*n-6)/2+"px"],top:[-(i-6)/2+"px",-(2*i-6)/2+"px"]},{duration:200,fill:"forwards"})})),t.addEventListener("mouseleave",(function(){o.classList.remove("motionPointer--hover"),o.animate({width:[2*n+"px",n+"px"],height:[2*i+"px",i+"px"],left:[-(2*n-6)/2+"px",-(n-6)/2+"px"],top:[-(2*i-6)/2+"px",-(i-6)/2+"px"]},{duration:200,fill:"forwards"})}))})})}document.addEventListener("mousemove",(function(t){let e=t.clientX,n=t.clientY;o.style.transform=`translate(${e}px, ${n}px)`}))}
