function motionPointerInit(e){let t=document.createElement("div");t.classList.add("motionPointer"),document.querySelector("body").appendChild(t);const o=document.querySelector(".motionPointer");let n=[];e.forEach(e=>{let t=document.querySelectorAll(e);return n.push(t),n}),n.forEach(e=>{e.forEach(e=>{e.addEventListener("mouseenter",(function(){o.classList.add("motionPointer--hover")})),e.addEventListener("mouseleave",(function(){o.classList.remove("motionPointer--hover")}))})}),document.addEventListener("mousemove",(function(e){let t=e.pageX,n=e.pageY;o.style.transform=`translate(${t}px, ${n}px)`}))}
